import numpy as np

def onStart(ctx):
    ctx['log']('python_script started')

def onState(ctx, field, value, tsMs=None):
    # Example: react to a state change.
    # ctx['log'](f'state {field}={value} tsMs={tsMs}')
    return

def onMsg(ctx, inputs):
    msg = inputs.get('msg')

    return {'outputs': {'out': msg}}

def _process(ctx, skeletons) -> dict:
    penis = None
    vagina = None

    for model in skeletons:
        for b in model['bones']:
            # ctx['log'](b)
            # break
            if b['name'] == 'MalePenisBase':
                penis = b
                break
            elif b['name'] == 'Vagina':
                vagina = b
                break

    if penis and vagina:
        p0 = np.array(penis['pos'])
        p1 = np.array(vagina['pos'])
        dist = np.linalg.norm(p0-p1)
    else:
        dist = 0
    return dist

def onExec(ctx, execIn, inputs):
    # Example: choose different exec outputs by execIn port name.
    if execIn == 'exec2':        
        return {'exec': ['exec2'], 'outputs': {'out': inputs.get('msg')}}

    return {'exec': ['exec'],  'outputs':{"out": _process(ctx, inputs.get("msg")) }}

def onStop(ctx):
    ctx['log']('python_script stopped')
